from django.contrib.auth.models import User, Groupfrom rest_framework import serializersfrom .models import LdapUser, LdapGroup, LdapRoles, LdapOAclass UserSerializer(serializers.HyperlinkedModelSerializer):    class Meta:        model = User        fields = ('url', 'username', 'email', 'groups')class GroupSerializer(serializers.HyperlinkedModelSerializer):    class Meta:        model = Group        fields = ('url', 'name')class Userserializer(serializers.ModelSerializer):    class Meta:        model = LdapUser        fields = ('username', 'user_sn', 'email', 'dn', 'password')class Groupserializer(serializers.ModelSerializer):    class Meta:        model = LdapGroup        fields = ('dn', 'name', 'ou')class Rolesserializer(serializers.ModelSerializer):    class Meta:        model = LdapRoles        fields = ('dn', 'cn')class OAserializer(serializers.ModelSerializer):    """    给OA系统提供组织权限结构修改    """    unMember = serializers.ListField(source='uniqueMember')    class Meta:        model = LdapOA        fields = ('cn', 'unMember')